(function ($) {
    $(document).ready(function () {
        $(".carousel").slick({
            dots: 0,
            infinite: 1,
            speed: 300,
            slidesToShow: 4,
            slidesToScroll: 4,
            variableWidth: 0
        });
        $(".form__input").focusout(function () {
            var e = $(this).val();
            "" === e ? $(this).removeClass("has-value") : $(this).addClass("has-value")
        });
        for (var youtube = document.querySelectorAll(".youtube"), i = 0; i < youtube.length; i++) {
            var source = "https://img.youtube.com/vi/" + youtube[i].dataset.embed + "/hqdefault.jpg", image = new Image;
            image.src = source, image.addEventListener("load", function () {
                youtube[i].appendChild(image)
            }(i)), youtube[i].addEventListener("click", function () {
                var e = document.createElement("iframe");
                e.setAttribute("frameborder", "0"), e.setAttribute("allowfullscreen", ""), e.setAttribute("src", "https://www.youtube.com/embed/" + this.dataset.embed + "?rel=0&showinfo=0&autoplay=1"), e.setAttribute("width", "100%"), e.setAttribute("height", "100%"), this.innerHTML = "", this.appendChild(e)
            })
        }
        
        
        var elem = document.querySelector(".js-switch");
        
        if (elem) {
            init = new Switchery(elem, {color: "#ff5e3a", secondaryColor: "#c2c5d9"});
        }

        if ($(".dropdown-item")) {
            $(".dropdown-item").click(function (e) {
            e.preventDefault();
            var t = $(this).text(), i = $(this).parent().siblings(".dropdown-toggle"), o = i.children(".label-value");
            o.text(t), i.addClass("dropdown__label_active")
        });
        }
       
            
    });
})(jQuery)
